package design_pattern.proxy.virtualProxy;

import java.net.MalformedURLException;
import java.net.URL;
import java.util.Enumeration;
import java.util.Hashtable;

import javax.swing.Icon;
import javax.swing.JFrame;
import javax.swing.JMenu;
import javax.swing.JMenuBar;
import javax.swing.JMenuItem;

public class ImageProxyTestDrive {

	ImageComponent imageComponent;
	JFrame jFrame=new JFrame("cd cover");
	JMenuBar jMenuBar;
	JMenu jMenu;
	Hashtable<String, String> cds=new Hashtable<>();
	
	public static void main(String[] args) {
		ImageProxyTestDrive testDrive=new ImageProxyTestDrive();
	}
	
	public ImageProxyTestDrive() {
		// TODO Auto-generated constructor stub
//		cds.put("youname", "data:image/jpeg;base64,");
		cds.put("youname", "https://resources.cloud.genuitec.com/wp-content/uploads/2018/03/debug_combo.png");
		try {
			URL initialUrl=new URL(cds.get("youname"));
			jMenuBar=new JMenuBar();
			jMenu=new JMenu();
			jMenuBar.add(jMenu);
			jFrame.add(jMenuBar);
			for(Enumeration<String> enumeration=cds.keys();enumeration.hasMoreElements();) {
				String name=enumeration.nextElement();
				JMenuItem jMenuItem=new JMenuItem(name);
				jMenu.add(jMenuItem);
				jMenuItem.addActionListener(event->{
					imageComponent.setIcon(new ImageProxy(getCDUrl(event.getActionCommand())));
					jFrame.repaint();
				});
			}			
			Icon icon=new ImageProxy(initialUrl);
			imageComponent=new ImageComponent(icon);
			jFrame.getContentPane().add(imageComponent);
			jFrame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
			jFrame.setSize(800,600);
			jFrame.setVisible(true);
		} catch (MalformedURLException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
	}

	private URL getCDUrl(String actionCommand) {
		// TODO Auto-generated method stub
		try {
			return new URL(cds.get(actionCommand));
		} catch (Exception e) {
			e.printStackTrace();
		}
		return null;
	}
	
}
